import React, { useEffect, useState } from "react";
import axiosClient from "../api/axiosClient";

function BookingManager() {
  const [bookings, setBookings] = useState([]);

  const load = () => {
    axiosClient.get("/bookings").then((res) => setBookings(res.data));
  };

  useEffect(load, []);

  const confirm = (id) => {
    axiosClient.put(`/bookings/${id}/confirm`).then(load);
  };

  const cancel = (id) => {
    axiosClient.put(`/bookings/${id}/cancel`).then(load);
  };

  return (
    <div>
      <h1>Quản lý Booking</h1>
      {bookings.map((b) => (
        <div key={b.id}>
          {b.customerName} - {b.status}
          <button onClick={() => confirm(b.id)}>Xác nhận</button>
          <button onClick={() => cancel(b.id)}>Hủy</button>
        </div>
      ))}
    </div>
  );
}

export default BookingManager;
