import React, { useEffect, useState } from "react";
import axiosClient from "../api/axiosClient";

function MenuManager() {
  const [menu, setMenu] = useState([]);
  const [form, setForm] = useState({
    name: "",
    price: "",
    description: "",
    imageUrl: "",
  });

  const load = () => {
    axiosClient.get("/menu").then((res) => setMenu(res.data));
  };

  useEffect(load, []);

  const create = async () => {
    await axiosClient.post("/menu", form);
    load();
  };

  const remove = async (id) => {
    await axiosClient.delete(`/menu/${id}`);
    load();
  };

  return (
    <div>
      <h1>Quản lý Menu</h1>

      <input placeholder="Tên món"
        onChange={(e) => setForm({ ...form, name: e.target.value })} />
      <input placeholder="Giá"
        onChange={(e) => setForm({ ...form, price: e.target.value })} />
      <input placeholder="Mô tả"
        onChange={(e) => setForm({ ...form, description: e.target.value })} />
      <input placeholder="Hình ảnh URL"
        onChange={(e) => setForm({ ...form, imageUrl: e.target.value })} />

      <button onClick={create}>Thêm món</button>

      {menu.map((m) => (
        <div key={m.id}>
          {m.name} - {m.price}
          <button onClick={() => remove(m.id)}>Xóa</button>
        </div>
      ))}
    </div>
  );
}

export default MenuManager;
