import React, { useEffect, useState } from "react";
import axiosClient from "./api/axiosClient";

function BookingManager() {
  const [bookings, setBookings] = useState([]);
  const [form, setForm] = useState({
    customer_name: "",
    phone: "",
    date: "",
    time: "",
    num_guests: "",
  });
  const [editing, setEditing] = useState(null);

  const load = () => {
    axiosClient.get("/api/bookings").then((res) => setBookings(res.data));
  };

  useEffect(load, []);

  const create = async () => {
    await axiosClient.post("/api/bookings", form);
    setForm({ customer_name: "", phone: "", date: "", time: "", num_guests: "" });
    load();
  };

  const update = async () => {
    await axiosClient.put(`/api/bookings/${editing.id}`, form);
    setEditing(null);
    setForm({ customer_name: "", phone: "", date: "", time: "", num_guests: "" });
    load();
  };

  const remove = async (id) => {
    await axiosClient.delete(`/api/bookings/${id}`);
    load();
  };

  const edit = (item) => {
    setEditing(item);
    setForm({
      customer_name: item.customer_name,
      phone: item.phone,
      date: item.date,
      time: item.time,
      num_guests: item.num_guests
    });
  };

  return (
    <div className="booking-manager">
      <h1>Quản lý Đặt bàn</h1>
      <div className="form">
        <input placeholder="Tên khách" value={form.customer_name}
          onChange={(e) => setForm({ ...form, customer_name: e.target.value })} />
        <input placeholder="Số điện thoại" value={form.phone}
          onChange={(e) => setForm({ ...form, phone: e.target.value })} />
        <input type="date" value={form.date}
          onChange={(e) => setForm({ ...form, date: e.target.value })} />
        <input type="time" value={form.time}
          onChange={(e) => setForm({ ...form, time: e.target.value })} />
        <input placeholder="Số khách" value={form.num_guests}
          onChange={(e) => setForm({ ...form, num_guests: e.target.value })} />
        {editing ? (
          <button onClick={update}>Cập nhật</button>
        ) : (
          <button onClick={create}>Thêm booking</button>
        )}
      </div>
      <div className="booking-list">
        {bookings.map((b) => (
          <div key={b.id} className="booking-item">
            <h3>{b.customer_name}</h3>
            <p>Phone: {b.phone}</p>
            <p>Date: {b.date} Time: {b.time}</p>
            <p>Guests: {b.num_guests}</p>
            <button onClick={() => edit(b)}>Sửa</button>
            <button onClick={() => remove(b.id)}>Xóa</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookingManager;
